$(function () {

    $('#btn_sign_in').on('click', function (e) {
        e.preventDefault();
            $('#frm_sign_in').submit();
    });
    $('#frm_sign_in').validate({
        errorClass: 'error-msg-validate',
        rules: {
            username:{
                required: true,
            },
            password:{
                required:true,
            }
        }
    });

});